answer = ifScript();

function ifScript() {
	var req = current.sys_id;
	var app_sys = "";
	var reqtype = "";
	var ritm = new GlideRecord('sc_req_item');
	ritm.addQuery('sys_id', req);
	ritm.query();
	
	if(ritm.next())
		{
		app_sys = ritm.cat_item.u_select_application_name;
		reqtype = ritm.cat_item.u_type_of_request;
	}
	workflow.scratchpad.application_name = app_sys;
	workflow.scratchpad.request_type = reqtype;
	
	if (app_sys !=  '') 
	{
		return 'yes';
	}
	return 'no';
}

