var app = current.variables.select_application;
var usr = current.variables.requested_for;
var count = 0;
var appsysid = "";
var application = new GlideRecord('x_14308_uam_user_a_user_to_application');
	application.addQuery('user',usr);
	application.addQuery('application',app);
	application.addQuery('active',true);
	application.query();
		if(application.next())
			{
				appsysid = application.sys_id;
				//gs.addInfoMessage(app1sysid);
			}
	var access = new GlideRecord('x_14308_uam_user_a_user_to_access');
	access.addQuery('user', usr);
	access.addQuery('application', appsysid);
	access.addQuery('active',true);
	access.query();
	// Check if an Active access already exist for the user, do nothing
	while(access.next())
		{
			count = count +1;
	}

	if(count <= 0)
		{
			application.active = false;
			application.removed_date = new GlideDateTime();
			application.removed_ritm = current.sys_id;
			application.update();
		}

